mapper.select:


     and file_id = #{fileId}
     and file_name like concat('%', #{fileName}, '%')
     and dept_id = #{deptId}
     and belongteam = #{belongteam}
     and download_userid = #{downloadUserid}
     and download_user = #{downloadUser}
     and DATE(download_time) = #{downloadTime}

ORDER BY download_time DESC

	SELECT COUNT(*) as download_count
    from dms_download_records
    where file_id = #{fileId}

mapper.update:
update dms_download_records
file_id = #{fileId}, file_name = #{fileName}, dept_id = #{deptId}, belongteam = #{belongteam}, download_userid = #{downloadUserid}, download_user = #{downloadUser}, download_time = #{downloadTime},
where id = #{id}
