mapper.select:
     select DISTINCT
     	fi.file_id,fi.file_name,fi.file_path,fi.author,fi.reviewer,fi.file_type,fi.file_size,fi.publish_id,
     	fi.file_status,fi.belongteam,fi.description,fi.update_by,fi.update_time,fpb.publish_time
 	from dms_file_info fi
 	inner join dms_file_permissions fp ON fi.file_id = fp.file_id
		inner join sys_user su ON fp.dept_id = su.dept_id
		INNER JOIN (SELECT file_id, publish_time FROM dms_file_publish WHERE is_current = 1) fpb ON fi.file_id = fpb.file_id









     	 		and (su.user_id = #{queryuserId} or su.dept_id in (select parent_id from sys_dept where dept_id = (select dept_id from sys_user where user_id = #{queryuserId})))


             	and fp.dept_id= #{queryuserDept}
             	and su.user_id = #{queryuserId}


             	and su.dept_id in (select parent_id from sys_dept where dept_id = (select dept_id from sys_user where user_id = #{queryuserId}))


          and fi.file_id = #{fileId}
          and file_name like concat('%', #{fileName}, '%')
          and file_path = #{filePath}
          and author = #{author}
          and reviewer = #{reviewer}
          and file_type = #{fileType}
          and file_size = #{fileSize}
          and file_status = #{fileStatus}
          and belongteam = #{belongteam}
          and description = #{description}
          and fi.update_by = #{updateBy}
          and DATE(fi.update_time) = #{updateTime}
          and fi.publish_id = #{publishId}
          and DATE(publish_time) = #{publishTime}

     ORDER BY publish_time DESC

mapper.update:
update dms_file_info
file_name = #{fileName}, file_path = #{filePath}, author = #{author}, reviewer = #{reviewer}, file_type = #{fileType}, file_size = #{fileSize}, file_status = #{fileStatus}, belongteam = #{belongteam}, description = #{description}, update_by = #{updateBy}, update_time = #{updateTime}, publish_id = #{publishId},
where file_id = #{fileId}
